  update(time) {
    if(this.protagonist){
      this.protagonist.update(this.cursors, time);
      this.enemiesMovementAndFire(time, this.protagonist, this.enemies); // Movimento dos Inimigos
      this.changeDoor(this.protagonist);
    }

    if (this.esc.isDown) {
      this.scene.stop();
      this.scene.start("Menu");
    }
  }

  coinSpin(){
    Phaser.Actions.Call(this.coins.getChildren(), function (coin) {
      coin.spinCoin();
    },this);
  }

  enemiesMovementAndFire(time, protagonist) {
    Phaser.Actions.Call(this.enemies.getChildren(), function (enemy) {
      enemy.animationEnemy();
      if (Math.abs(enemy.x - this.protagonist.x) <= 200 && Math.abs(enemy.y - this.protagonist.y) <= 50) {
        enemy.stopMovement(protagonist.x);
        enemy.fire(time, this.protagonist);
      } else {
        enemy.movement();
      }
    }, this);
  }

  killProtagonist() {
    if(this.protagonist.lives-1==0){
      this.scene.stop();
      this.sound.stopAll();
      this.scene.start("GameOver");
    }else{
      this.protagonist.killed();
    }
  }

